def knapsack(val, wt, W, n):
    if n == 0 or W == 0:
        return 0
    if (wt[n-1] > W):
        return knapsack(val, wt, W, n-1)
    else:
        return max(val[n-1] + knapsack(val, wt, W-wt[n-1], n-1),
                   knapsack(val, wt, W, n-1))

