def fib(n):
    if n <= 1: return n
    return fib(n-1) + fib(n-2)
print(fib(5))

memo = {0:0, 1:1}
def fib_dp(n):
    if n not in memo:
        memo[n] = fib_dp(n-1) + fib_dp(n-2)
    return memo[n]

